                    <rect y="0.5" width="40" height="40" rx="20" fill="#43C065" />
                    <rect x="7" y="7.5" width="26" height="25.4" rx="12.7" fill="white" />
                    <path d="M18.7071 24.1929C18.3166 24.5834 17.6834 24.5834 17.2929 24.1929L14.7 21.6C14.3134 21.2134 14.3134 20.5866 14.7 20.2V20.2C15.0866 19.8134 15.7134 19.8134 16.1 20.2L17.2929 21.3929C17.6834 21.7834 18.3166 21.7834 18.7071 21.3929L23.9 16.2C24.2866 15.8134 24.9134 15.8134 25.3 16.2V16.2C25.6866 16.5866 25.6866 17.2134 25.3 17.6L18.7071 24.1929Z" fill="#43C065" />
